    $(document).ready(function() {
      // Toggle boxes
      $(".hid").click(function(e) {
        e.stopPropagation();
        let targetId = $(this).data("hidden"); // get the target box id
        $(".box").not("#" + targetId).addClass("hidden"); // hide all other boxes
        $("#" + targetId).toggleClass("hidden"); // toggle current one
      });

      $(".box").click(function(e) {
        e.stopPropagation(); // prevent closing when clicking inside
      });

      $(document).click(function() {
        $(".box").addClass("hidden"); // close all when clicking outside
      });

      // jQuery UI Tabs
      $("#tabs").tabs();
    });
