
  $(document).ready(function() {
    // Toggle boxes
    $(".toggle-btn").click(function(e) {
      e.stopPropagation();
      let targetId = $(this).data("hidden"); // custom attribute
      $(".box").not("#" + targetId).addClass("hidden"); // hide others
      $("#" + targetId).toggleClass("hidden"); // toggle selected
    });

    $(".box").click(function(e) {
      e.stopPropagation(); // prevent closing when clicking inside
    });

    $(document).click(function() {
      $(".box").addClass("hidden"); // close when clicking outside
    });

    // jQuery UI Tabs
    $("#tabs").tabs();
  });
